mapper.select:
select *
from userfile
where UID = #{UID}
  and fileDir = #{fileDir}
  and fileState = 'Y';

mapper.update:
update userfile
set fileState = 'N'
where UID = #{UID}
  and fileName = #{fileName}
  and fileDir = #{fileDir};

mapper.select:
select *
from userfile
where UID = #{UID}
  and fileState = 'N';

mapper.delete:
delete
from userfile
where UID = #{UID}
  and fileState = 'N';

delete
from userfile
where UID = #{UID}
  and fileName = #{fileName}
  and fileDir = #{fileDir}
  and fileState = 'N';
